import styles from './styles.module.css';
import { Navigate } from 'react-router-dom';
import Main from '../Main/index.jsx';
import React, { useEffect, useState } from 'react';

function ProfileCard({ user }) {
  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Profile</h5>
        <p className="card-text">
          <strong>Name:</strong> {user.name}
        </p>
        <p className="card-text">
          <strong>Email:</strong> {user.email}
        </p>
        <img src={user.photo} className="card-img-top" alt="User" />
      </div>
    </div>
  );
}
const Profile = () => {
  const [user, setUser] = useState({});
  const [newPassword, setNewPassword] = useState('');
  const [newPhoto, setNewPhoto] = useState('');
  const [message, setMessage] = useState('');

  //   useEffect(() => {
  //     // Aqui você faria uma requisição para o servidor para buscar as informações do usuário logado
  //     axios
  //       .get('/api/user')
  //       .then((response) => {
  //         setUser(response.data);
  //       })
  //       .catch((error) => {
  //         console.error('Erro ao buscar informações do usuário:', error);
  //       });
  //   }, []);

  const handlePasswordChange = (e) => {
    setNewPassword(e.target.value);
  };

  const handlePhotoChange = (e) => {
    setNewPhoto(e.target.value);
  };

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     // Aqui você faria uma requisição para o servidor para atualizar as informações do usuário
  //     axios
  //       .put('/api/user', { newPassword, newPhoto })
  //       .then((response) => {
  //         setMessage('Informações atualizadas com sucesso!');
  //       })
  //       .catch((error) => {
  //         console.error('Erro ao atualizar informações do usuário:', error);
  //       });
  //   };
  const handleSubmit = (e) => {};
  return (
    <>
      <Main />
      <div>
        <h1>Editar Perfil</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nova Senha:</label>
            <input
              type="password"
              value={newPassword}
              onChange={handlePasswordChange}
            />
          </div>
          <div>
            <label>Nova Foto:</label>
            <input type="text" value={newPhoto} onChange={handlePhotoChange} />
          </div>
          <button type="submit">Salvar Alterações</button>
        </form>
        {message && <p>{message}</p>}
      </div>{' '}
    </>
  );
};

export default Profile;
