import styles from './styles.module.css';
import { Navigate } from 'react-router-dom';
import Main from '../Main/index.jsx';
import React, { useEffect, useState } from 'react';

function ProfileCard({ user }) {
  return (
    <div className={styles.card}>
      <div className={styles.cardBody}>
        <h5 className={styles.cardTitle}>Profile</h5>
        <p className={styles.cardText}>
          <strong>Name:</strong> {user.name}
        </p>
        <p className={styles.cardText}>
          <strong>Email:</strong> {user.email}
        </p>
        <img src={user.photo} className={styles.cardImgTop} alt="User" />
      </div>
    </div>
  );
}
const Profile = () => {
  const [user, setUser] = useState({});
  const [newPassword, setNewPassword] = useState('');
  const [newPhoto, setNewPhoto] = useState('');
  const [message, setMessage] = useState('');

  //   useEffect(() => {
  //     // Aqui você faria uma requisição para o servidor para buscar as informações do usuário logado
  //     axios
  //       .get('/api/user')
  //       .then((response) => {
  //         setUser(response.data);
  //       })
  //       .catch((error) => {
  //         console.error('Erro ao buscar informações do usuário:', error);
  //       });
  //   }, []);

  const handlePasswordChange = (e) => {
    setNewPassword(e.target.value);
  };

  const handlePhotoChange = (e) => {
    setNewPhoto(e.target.value);
  };
  function showPreview(event) {
    if (event.target.files.length > 0) {
      var src = URL.createObjectURL(event.target.files[0]);
      var preview = document.getElementById('file-ip-1-preview');
      preview.src = src;
      preview.style.display = 'block';
    }
  }

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     // Aqui você faria uma requisição para o servidor para atualizar as informações do usuário
  //     axios
  //       .put('/api/user', { newPassword, newPhoto })
  //       .then((response) => {
  //         setMessage('Informações atualizadas com sucesso!');
  //       })
  //       .catch((error) => {
  //         console.error('Erro ao atualizar informações do usuário:', error);
  //       });
  //   };
  const handleSubmit = (e) => {};
  return (
    <div className={styles.container}>
      <div className={styles.row}>
        <div className={styles.colMd6}>
          <ProfileCard user={user} />
        </div>
        <div className={styles.colMd6}>
          <div className={styles.card}>
            <div className={styles.cardBody}>
              <h5 className={styles.cardTitle}>Edit Profile</h5>
              <form
                onSubmit={handleSubmit}
                className={styles.manageProfileForm}
              >
                <div className={styles.center}>
                  <div className={styles.formInput}>
                    <div className={styles.preview}>
                      <img id="file-ip-1-preview" alt="Preview" />
                    </div>
                    <label htmlFor="file-ip-1">Upload Image</label>
                    <input
                      type="file"
                      id="file-ip-1"
                      accept="image/*"
                      onChange={showPreview}
                      className={styles.formControl}
                    />
                  </div>
                </div>

                <div className={styles.manageProfileTxtField}>
                  <label htmlFor="" className={styles.manageProfileLabel}>
                    name
                  </label>
                  <input
                    type="text"
                    name=""
                    id=""
                    className={styles.formControl}
                  />
                </div>
                <div className={styles.manageProfileTxtField}>
                  <label htmlFor="" className={styles.manageProfileLabel}>
                    username
                  </label>
                  <input
                    type="text"
                    name=""
                    id=""
                    className={styles.formControl}
                  />
                </div>

                <div className={styles.manageProfileTxtField}>
                  <label htmlFor="" className={styles.manageProfileLabel}>
                    email
                  </label>
                  <input
                    type="text"
                    name=""
                    id=""
                    className={styles.formControl}
                  />
                </div>

                <div className={styles.manageProfileTxtField}>
                  <label htmlFor="" className={styles.manageProfileLabel}>
                    phone
                  </label>
                  <input
                    type="text"
                    name=""
                    id=""
                    className={styles.formControl}
                  />
                </div>

                <div className={styles.manageProfileTxtField}>
                  <label htmlFor="" className={styles.manageProfileLabel}>
                    job
                  </label>
                  <input
                    type="text"
                    value="doctor"
                    disabled
                    className={styles.formControl}
                  />
                </div>

                <div className={styles.manageProfileTxtField}>
                  <label htmlFor="" className={styles.manageProfileLabel}>
                    description
                  </label>
                  <textarea
                    name=""
                    id=""
                    cols="30"
                    rows="10"
                    className={styles.formControl}
                  ></textarea>
                </div>
                <button type="submit" className={styles.btn}>
                  Update Profile
                </button>
              </form>
              {message && <p className={styles.message}>{message}</p>}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
