import styles from './styles.module.css';
import { Navigate } from 'react-router-dom';
import Main from '../Main/index.jsx';
import React, { useEffect, useState } from 'react';

const Profile = () => {
  const [user, setUser] = useState({});
  const [newPassword, setNewPassword] = useState('');
  const [newPhoto, setNewPhoto] = useState('');
  const [message, setMessage] = useState('');

  //   useEffect(() => {
  //     // Aqui você faria uma requisição para o servidor para buscar as informações do usuário logado
  //     axios
  //       .get('/api/user')
  //       .then((response) => {
  //         setUser(response.data);
  //       })
  //       .catch((error) => {
  //         console.error('Erro ao buscar informações do usuário:', error);
  //       });
  //   }, []);

  const handlePasswordChange = (e) => {
    setNewPassword(e.target.value);
  };

  const handlePhotoChange = (e) => {
    setNewPhoto(e.target.value);
  };
  function showPreview(event) {
    if (event.target.files.length > 0) {
      var src = URL.createObjectURL(event.target.files[0]);
      var preview = document.getElementById('file-ip-1-preview');
      preview.src = src;
      preview.style.display = 'block';
    }
  }

  //   const handleSubmit = (e) => {
  //     e.preventDefault();
  //     // Aqui você faria uma requisição para o servidor para atualizar as informações do usuário
  //     axios
  //       .put('/api/user', { newPassword, newPhoto })
  //       .then((response) => {
  //         setMessage('Informações atualizadas com sucesso!');
  //       })
  //       .catch((error) => {
  //         console.error('Erro ao atualizar informações do usuário:', error);
  //       });
  //   };
  const handleSubmit = (e) => {};
  return (
    <>
      <Main />
      <div className={styles.manage_profile}>
        <div class="manage-profile-title">manage account</div>
        <hr />
        <div class="edit-profile-section">
          <div class="manage-profile-subtitle">edit profile</div>
          <form action="" method="post" class="manage-profile-form">
            <div class="center">
              <div class="form-input">
                <div class="preview">
                  <img id="file-ip-1-preview" />
                </div>
                <label for="file-ip-1">Upload Image</label>
                <input
                  type="file"
                  id="file-ip-1"
                  accept="image/*"
                  onChange="showPreview(event);"
                />
              </div>
            </div>

            <div class="manage-profile-txt-field">
              <label for="" class="manage-profile-label">
                name
              </label>
              <input type="text" name="" id="" />
            </div>
            <div class="manage-profile-txt-field">
              <label for="" class="manage-profile-label">
                username
              </label>
              <input type="text" name="" id="" />
            </div>

            <div class="manage-profile-txt-field">
              <label for="" class="manage-profile-label">
                email
              </label>
              <input type="text" name="" id="" />
            </div>

            <div class="manage-profile-txt-field">
              <label for="" class="manage-profile-label">
                phone
              </label>
              <input type="text" name="" id="" />
            </div>

            <div class="manage-profile-txt-field">
              <label for="" class="manage-profile-label">
                job
              </label>
              <input type="text" value="doctor" disabled />
            </div>

            <div class="manage-profile-txt-field">
              <label for="" class="manage-profile-label">
                description
              </label>
              <textarea name="" id="" cols="30" rows="10"></textarea>
            </div>
            <a href="" class="btn">
              update profile
            </a>
          </form>
        </div>
        <hr />
        <div class="manage-profile-subtitle">change password</div>

        <form action="" method="post" class="manage-profile-form">
          <div class="manage-profile-txt-field">
            <label for="" class="manage-profile-label">
              old password
            </label>
            <input type="password" name="" id="" />
          </div>
          <div class="manage-profile-txt-field">
            <label for="" class="manage-profile-label">
              new password
            </label>
            <input type="password" name="" id="" />
          </div>
          <div class="manage-profile-txt-field">
            <label for="" class="manage-profile-label">
              confirm new password
            </label>
            <input type="password" name="" id="" />
          </div>
          <a href="" class="btn">
            update new password
          </a>
        </form>
        <hr>
          <div class="manage-profile-subtitle">delete account</div>

          <div class="manage-profile-close">
            <p>
              {' '}
              When you delete your account, you will not be able to retrieve the
              content or information that you have shared.
              <span> Warning:</span>Closing your account is irreversible.
            </p>
            <a href="" class="btn">
              delete account
            </a>
          </div>
        </hr>
      </div>
    </>
  );
};

export default Profile;
